class CookieGenerator{rank(){const prods=this.getProducts();console.table(prods.slice(0,5))}constructor(){this.timers={};this.elms={big:document.querySelector("#bigCookie"),get shimmer(){return document.querySelector("#shimmers .shimmer")}};const that=this;function buyWishedOne(){const prods=that.getProducts();if(Game.cookies>=prods[0].price){prods[0].elm.click()}}function clickGoldenCookie(){if(that.elms.shimmer){that.elms.shimmer.click()}}function clickBigCookie(){that.elms.big.click()}this.timers.buyer=setInterval(buyWishedOne,100);this.timers.shimmer=setInterval(clickGoldenCookie,3e3);this.timers.clickBigCookie=setInterval(clickBigCookie,0)}getProducts(){const prods=Game.ObjectsById.map(p=>{let cps=p.cps(p);return{name:p.name,cps:cps,price:p.bulkPrice,elm:p.l,costPerf:cps/p.price}});prods.sort((a,b)=>b.costPerf-a.costPerf);return prods}clearAllIntervals(){for(let timer in this.timers){clearInterval(this.timers[timer]);delete this.timers[timer]}}}let cg=new CookieGenerator;